div.script:
window.addEventListener('DOMContentLoaded', () => {
  if (window.matchMedia('(min-width: 750px)').matches) {
    return;
  }

  const handleCollapse = (heading) => {
    const isOpen = heading.classList.contains('block-collapsed');
    heading.classList.toggle('block-collapsed', !isOpen);
    heading.setAttribute('aria-expanded', isOpen ? 'false' : 'true');
  };

  document.querySelectorAll('.grid .footer-block__heading').forEach((heading, index) => {
    heading.classList.add('block-collapsed_new');
    heading.setAttribute('role', 'button');
    heading.setAttribute('aria-expanded', 'false');
    heading.setAttribute('tabindex', '1');

    heading.nextElementSibling.setAttribute('id', `footer-block-index-${index}`);
    heading.setAttribute('aria-controls', `footer-block-index-${index}`);

    handleCollapse(heading); // Close all menus initially

    heading.addEventListener('click', () => {
      handleCollapse(heading);
    });
    heading.addEventListener('keypress', () => {
      handleCollapse(heading);
    });
  });
});

